//JavaScript
//Global Initialization
function Floor26 () {
    if (!ClearBlade.Floor26) {
        ClearBlade.Floor26 = {
            allowCoordinateUpdates: false,
            databoxHeight: 15,
            databoxWidth: 60,
            imgURL: "https://s3.amazonaws.com/uploads.hipchat.com/76688/4619737/itJvIwLJHDaYHk5/26th%20floor.png",
            primary_display_textcolor: "#000000",
            showSensorName: false,
            dataTextStyle: "bold 12px Verdana",
            deviceTextStyle: "bold 10px Verdana",
            ds_sensorGroup: "SensorGroup",
            ds_xy: "updateSensorCoordinates",
            dsCurrent: "currentData",
            dsSensorGroup: "SensorGroup",
            SCALE: 1,
            blockUpdates: false
        };
    }

    //ClearBlade.Floor26.currentData = this.datasource;
    ClearBlade.Floor26.currentData = datasources.currentData.latestData() ;
    ClearBlade.Floor26.LightData = datasources.LightData.latestData() ;
    //ClearBlade.Floor26.Assets = datasources.Assets.latestData() ; 

    var stage;
    var bgChild ; // this is the main map
    var hvacChild = [] ; // holder for all HVAC icons
    var lightChild = [] ; // holder for lighting

    var calculateAspectRatioFit = function(srcWidth, srcHeight, maxWidth, maxHeight) {
        var ratio = Math.min(maxWidth / srcWidth, maxHeight / srcHeight);
        var rtnWidth = srcWidth * ratio;
        var rtnHeight = srcHeight * ratio;
        return { width: rtnWidth, height: rtnHeight };
    };

    window.addEventListener('resize', function(event) {
        drawCanvas();
    });

    var drawCanvas = function() {
        stage = new createjs.Stage("dataMap");
        stage.removeAllChildren();
      //  stage.enableMouseOver(5); //Required for enabling mouseover events
        var bgImg = document.createElement("img");
        //Hack to make the sizing work to the HTMLWidget size
        stage.canvas.width = stage.canvas.parentElement.parentElement.parentElement.parentElement.clientWidth;
        stage.canvas.height = stage.canvas.parentElement.parentElement.parentElement.parentElement.clientHeight;

        //console.log (foo[0].data);
        //var rawDataString = ClearBlade.Floor26.Assets[0].data.image;
        //bgImg.src = "data:image/png+xml;base64," + rawDataString ; // ClearBlade.Floor26.imgBlob;

        bgImg.src = ClearBlade.Floor26.imgURL;
        bgImg.onload = function() {
            var bg = new createjs.Bitmap(bgImg);
            var backgroundImageSize = calculateAspectRatioFit(bgImg.width, bgImg.height, stage.canvas.width, stage.canvas.height);
            //console.log("SCALE:" + backgroundImageSize.width / bgImg.width);
            ClearBlade.Floor26.SCALE = backgroundImageSize.width / bgImg.width;
            bg.scaleX = backgroundImageSize.width / bgImg.width;
            bg.scaleY = backgroundImageSize.width / bgImg.width;
            stage.removeAllChildren();
            bgChild = stage.addChild(bg);  // save the image for redrawing
            drawHvacData();  // default is HVAC data turned on
            //drawLightData();
            if (!ClearBlade.Floor26.blockUpdates)
                stage.update();
        };
    };

    var drawHvacData = function() {
        //draw the icons
        if (ClearBlade.Floor26.currentData !== undefined) {
            for (i = 0; i < ClearBlade.Floor26.currentData.results.length; i++) {
                drawDataWidget(ClearBlade.Floor26.currentData.results[i]);
            }
        }
        stage.update();
    };

    var drawLightData = function() {
        //draw the icons
        console.log("in LightData");
        if (ClearBlade.Floor26.LightData !== undefined) {
            console.log("drawLightData: LightData length=" + ClearBlade.Floor26.LightData.results.length);
            for (i = 0; i < ClearBlade.Floor26.LightData.results.length; i++) {
                drawDataWidget(ClearBlade.Floor26.LightData.results[i]);
            }
        }
        stage.update();
    };

    //var toggleLight = function(value) {
    window.toggleLight = function(value) {
        //draw the icons
        if (value) {
            drawLightData();
        } else {
            var i;
            var ctr = 0;
            for ( i in lightChild ) {
                ctr++;
                stage.removeChild(lightChild[i]) ;
                delete lightChild[i] ;
            }
            console.log("toggleLight: LightChild length=" + ctr );
            stage.update();
        }
    };

    //var toggleLight = function(value) {
    window.toggleHVAC = function(value) {
        //draw the icons
        if (value) {
            drawHvacData();
        } else {
            var i;
            var ctr = 0;
            for ( i in hvacChild ) {
                ctr++;
                stage.removeChild(hvacChild[i]) ;
                delete hvacChild[i] ;
            }
            stage.update();
        }
    };

    var drawDataWidget = function(textObj) {
        var deviceDetailsContainer;
        var boxheight = ClearBlade.Floor26.databoxHeight;
        var boxwidth = ClearBlade.Floor26.databoxWidth;
        stage.mouseMoveOutside = false;
        var shape = new createjs.Shape();
        var wcMain = new createjs.Container();
        var dataPointContainer = new createjs.Container();
        //Create Container for displaying value
        if (textObj.type == "light" ) {
            shape.graphics.beginStroke("black").beginFill(textObj.backgroundColor).drawCircle(0, 0, boxheight);
        } else {
            shape.graphics.beginStroke("black").beginFill(textObj.backgroundColor).drawRect(0, 0, boxwidth, boxheight);
        }
        var dv = "";
        if (isNaN(textObj.present_value))
            dv = textObj.present_value;
        else if ( textObj.type == "light" ) {
            dv = textObj.present_value ;
        } else
            dv = parseFloat(textObj.present_value).toFixed(2);
        //Value Object
        var valueText = new createjs.Text(dv, ClearBlade.Floor26.dataTextStyle, ClearBlade.Floor26.primary_display_textcolor);
        valueText.textAlign = "center";
        valueText.textBaseline = "middle";
        if ( textObj.type != "light" ) {
            valueText.x = boxwidth - boxwidth / 2;
            valueText.y = boxheight - boxheight / 2;
        }
        dataPointContainer.addChild(shape, valueText);
        //dataPointContainer.y = boxheight * i;
        wcMain.addChild(dataPointContainer);
        //Device Name Object
        deviceDetailsContainer = new createjs.Text(textObj.name, ClearBlade.Floor26.deviceTextStyle, ClearBlade.Floor26.primary_display_textcolor);
        deviceDetailsContainer.textAlign = "center";
        deviceDetailsContainer.visible = ClearBlade.Floor26.showSensorName;
        deviceDetailsContainer.x = (boxwidth - boxwidth / 2);
        deviceDetailsContainer.y = (boxheight - boxheight / 2) + 7;
        wcMain.addChild(deviceDetailsContainer);

        wcMain.name = textObj.name;
        wcMain.x = textObj.x * ClearBlade.Floor26.SCALE;
        wcMain.y = textObj.y * ClearBlade.Floor26.SCALE;
        wcMain.deviceData = textObj;

        if ( textObj.type == "light" ) {
            lightChild[textObj.name] = stage.addChild(wcMain);  // store the location in the global
        } else {
            hvacChild[textObj.name] = stage.addChild(wcMain);  // store the location in the global
        }

        if (ClearBlade.Floor26.allowCoordinateUpdates) {
            wcMain.on("pressmove", function(evt) {
                ClearBlade.Floor26.blockUpdates = true; //Block updating the view (causes a redraw)
                evt.currentTarget.x = evt.stageX;
                evt.currentTarget.y = evt.stageY;
                this.children[(this.children.length - 1)].visible = true;
                stage.update();
            });

            wcMain.on("pressup", function(evt) {
                if (ClearBlade.Floor26.allowCoordinateUpdates) {
                    ClearBlade.Floor26.blockUpdates = false;
                    evt.currentTarget.x = evt.stageX;
                    evt.currentTarget.y = evt.stageY;
                    this.children[(this.children.length - 1)].visible = ClearBlade.Floor26.showSensorName;
                    stage.update();
                    datasources[ClearBlade.Floor26.ds_xy].sendData({ "name": this.name, "x": evt.currentTarget.x / ClearBlade.Floor26.SCALE, "y": evt.currentTarget.y / ClearBlade.Floor26.SCALE });
                }
            });
        } else {
            wcMain.on("click", function(evt) {
                datasources[ClearBlade.Floor26.ds_sensorGroup].sendData({ "floor": this.deviceData.floor, "group": this.deviceData.groupid });
            });
        }

        wcMain.on("mouseover", function(evt) {
            this.children[(this.children.length - 1)].visible = true;
            stage.update();
        });
        wcMain.on("mouseout", function(evt) {
            this.children[(this.children.length - 1)].visible = false;
            stage.update();
        });

        //stage.update();
    };

    drawCanvas();

} // end of Floor26

