var self = this;
var incrementerControl = function(defaultValue) {
    var ic='<input type="numbers" class="text-right" value="' + parseFloat(defaultValue).toFixed(2) + '">';
    return ic;
};

var aiControl = function(defaultValue) {
    var ai = '<select class="">';
    switch (defaultValue) {
        case 'active':
            ai += '<option selected>active</option>';
            ai += '<option>inactive</option>';
            break;
        default:
            ai += '<option>active</option>';
            ai += '<option selected>inactive</option>';
    }
    ai += '</select>';
    return ai;
};

var isNumeric = function(n) {
    return !isNaN(parseFloat(n)) && isFinite(n);
};

var renderWidget=function() {
    var ld;
    var ds = self.datasource;
    var tmp = "";
    ld = document.getElementById("listDiv");
    if (ds.results) {
        tmp = "<div>Group ID " + ds.results[0].groupid + "<button type='button' class='btn btn-primary btn-xs float-right'>Save Changes</button></div>";
        tmp += "<table class='table table-condensed table-striped table-hover table-responsive x-small'>";
        for (var i = 0; i < ds.results.length; i++) {
            tmp += "<tr><td>" + ds.results[i].description + "</td><td class='text-xs-right text-right'>";
            if (isNumeric(ds.results[i].present_value)) {
                tmp += incrementerControl(ds.results[i].present_value);
            } else {
                tmp += aiControl(ds.results[i].present_value);
            }
            tmp += "</td></tr>";
        }
        tmp += "</table>";
        ld.innerHTML = tmp;
        $('.selectpicker').selectpicker();
    }
};

