            //drawElevatorData();  // default is Elevator data turned on

            if (!ClearBlade.Building10.blockUpdates)
                stage.update();
        };

        loadElevators() ;  // load global array
        createjs.Ticker.on ("tick", TickFunction) ;
        createjs.Ticker.interval = ClearBlade.Building10.AnimationTime ; 

    };

    var TickFunction = function (event) {

        var ctr = 0;
        for ( var i in elevatorChild ) {  // blase the old elevator location
            stage.removeChild(elevatorChild[i]) ;
            delete elevatorChild[i] ;
            ctr++ ;
        }
        //console.log ("TickFunction: elevatorChild array length: " + ctr );
        //console.log ("TickFunction: elevator array length: " + elevator.length );

        for ( var i=0; i < elevator.length; i++ ) {

            // move elevator
            if ( elevator[i].y < elevator[i].top && elevator[i].y >= elevator[i].bottom && elevator[i].direction == "down" ) {  // lower the elevator
                elevator[i].y = elevator[i].y + ClearBlade.Building10.elevationInterval ;
            } else if ( elevator[i].y > elevator[i].bottom && elevator[i].y <= elevator[i].top && elevator[i].direction == "up" ) {  // raise the elevator
               elevator[i].y = elevator[i].y - ClearBlade.Building10.elevationInterval ;
            } else if ( elevator[i].y >= elevator[i].top ) {
                elevator[i].direction = "up";
                elevator[i].y = elevator[i].y - ClearBlade.Building10.elevationInterval;
            } else if ( elevator[i].y <= elevator[i].bottom ) {
                elevator[i].direction = "down" ;
                elevator[i].y = elevator[i].y + ClearBlade.Building10.elevationInterval ;
            }

            //console.log ("TickFunction: elevator[" + i + "][x,y] = [" + elevator[i].x + "][ " + elevator[i].y + "]" );

        } // end of for 

